import React,{useState,useContext} from 'react';
import { TweetContext } from './TweetContext';

const AddTweet = () => {
const [title, setTitle] = useState("");
const [content, setContent] = useState("");
const [tweets , setTweet] = useContext(TweetContext);

const updateTitle = (e) => {
    setTitle(e.target.value);
}
const updateContent = (e) =>{
    setContent(e.target.value);
}
const addTweet = (e) =>{
    e.preventDefault();
    setTweet([prevTweets =>[...prevTweets,{title:title, content:content, like:0, id:tweets.length}]]);
    const allTweets = tweets;
    allTweets.push({title:title, content:content, like:0, id:tweets.lenght});
    localStorage.setItem('tweets',JSON.stringify(tweets));
}
return(
    <form onSubmit={addTweet}>
        <input type='text' name='title' value={title} onChange={updateTitle}></input>
        <input type='text' name='content' value={content} onChange={updateContent}/>
        <button>Submit</button>

    </form>
)
}
export default AddTweet
