import './Tweet.css';
import { useState } from 'react';

const Tweet = (props) => {
    const [likes, setLike] = useState(0)
    const upVote = () =>{
        setLike(likes+1);
    }
    const downVote = () =>{
        if(likes >= 1){
        setLike(likes-1);
        }
        if(likes == 0){
            setLike(likes-0);
        }
    }
;    return(
        <div className="Tweet">
        <h1>{props.title}</h1>
        <p>{props.content}</p>
        <p>Likes : {likes}</p>
        <button onClick={upVote}>Up voate!</button>
        <button onClick={downVote}>Down vote!</button>
        </div>
    );
}

export default Tweet;
